/* eslint-disable */
<template>
  <div id="app">
    <!-- 优化后的头部 -->
    <header class="bg-gradient-primary text-white py-4 shadow-lg" style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);">
      <div class="container">
        <div class="row align-items-center">
          <div class="col-md-6">
            <h1 class="h2 mb-0 fw-bold"><i class="fas fa-blog me-3"></i>我的个人博客</h1>
            <p class="mb-0 mt-2 opacity-75">记录生活，分享思考</p>
          </div>
          <div class="col-md-6 text-md-end">
            <button class="btn btn-light btn-lg rounded-pill px-4" @click="showCreateForm = !showCreateForm">
              <i class="fas fa-plus-circle me-2"></i>{{ showCreateForm ? '取消' : '写文章' }}
            </button>
          </div>
        </div>
      </div>
    </header>

    <main class="container my-5">
      <!-- 发布文章表单 -->
      <div v-if="showCreateForm" class="card mb-5 shadow-lg border-0 rounded-3">
        <div class="card-header bg-white py-3 border-0">
          <h3 class="mb-0 text-primary fw-bold"><i class="fas fa-edit me-2"></i>发布新文章</h3>
        </div>
        <div class="card-body">
          <form @submit.prevent="createPost">
            <div class="mb-4">
              <label for="title" class="form-label fw-bold">标题</label>
              <input type="text" class="form-control form-control-lg rounded-3" id="title" v-model="newPost.title" placeholder="请输入文章标题" required>
            </div>
            <div class="mb-4">
              <label for="author" class="form-label fw-bold">作者</label>
              <input type="text" class="form-control rounded-3" id="author" v-model="newPost.author" placeholder="请输入您的名字" required>
            </div>
            <div class="mb-4">
              <label for="content" class="form-label fw-bold">内容</label>
              <textarea class="form-control rounded-3" id="content" rows="8" v-model="newPost.content" placeholder="请输入文章内容" required></textarea>
            </div>
            <div class="d-grid gap-2 d-md-flex justify-content-md-end">
              <button type="button" class="btn btn-outline-secondary rounded-pill px-4" @click="showCreateForm = false">取消</button>
              <button type="submit" class="btn btn-primary rounded-pill px-4">
                <i class="fas fa-paper-plane me-2"></i>发布文章
              </button>
            </div>
          </form>
        </div>
      </div>

      <!-- 文章列表 -->
      <div class="row">
        <div class="col-md-8">
          <div v-if="loading" class="text-center py-5">
            <div class="spinner-border text-primary" role="status">
              <span class="visually-hidden">加载中...</span>
            </div>
            <p class="mt-3 text-muted">正在加载文章...</p>
          </div>

          <div v-else>
            <div v-if="posts.length === 0" class="text-center py-5">
              <div class="display-1 text-muted mb-3"><i class="fas fa-file-alt"></i></div>
              <h4 class="text-muted">暂无文章</h4>
              <p class="text-muted">快来发表第一篇文章吧！</p>
            </div>

            <article v-for="post in posts" :key="post.id" class="card mb-4 shadow-sm border-0 rounded-3 hover-effect">
              <div class="card-body p-4">
                <h2 class="card-title h3 fw-bold text-dark">{{ post.title }}</h2>
                <div class="d-flex justify-content-between text-muted mb-3">
                  <small><i class="fas fa-user me-1"></i>{{ post.author }}</small>
                  <small><i class="far fa-clock me-1"></i>{{ formatDate(post.createdTime) }}</small>
                </div>
                <p class="card-text text-muted lh-lg">{{ truncateContent(post.content, 150) }}</p>
                <div class="text-end">
                  <button class="btn btn-outline-primary btn-sm rounded-pill" @click="viewFullPost(post)">
                    阅读全文 <i class="fas fa-arrow-right ms-1"></i>
                  </button>
                </div>
              </div>
            </article>
          </div>
        </div>

        <!-- 优化后的侧边栏 -->
        <div class="col-md-4">
          <div class="card shadow-sm border-0 rounded-3 sticky-top" style="top: 20px;">
            <div class="card-header bg-primary text-white py-3 rounded-top-3">
              <h5 class="mb-0 fw-bold"><i class="fas fa-info-circle me-2"></i>关于博客</h5>
            </div>
            <div class="card-body">
              <p class="lead">欢迎来到我的个人博客！</p>
              <p>这里是一个简洁美观的博客系统，访客可以直接发表文章，无需注册登录。</p>
              <hr>
              <h6 class="fw-bold">博客特色：</h6>
              <ul class="list-unstyled">
                <li class="mb-2"><i class="fas fa-check-circle text-success me-2"></i>简洁美观的设计</li>
                <li class="mb-2"><i class="fas fa-check-circle text-success me-2"></i>响应式布局</li>
                <li class="mb-2"><i class="fas fa-check-circle text-success me-2"></i>支持访客发帖</li>
                <li class="mb-2"><i class="fas fa-check-circle text-success me-2"></i>实时预览</li>
              </ul>
              <div class="text-center mt-4">
                <div class="badge bg-light text-dark">文章总数: {{ posts.length }}</div>
              </div>
            </div>
          </div>

          <!-- 新增统计卡片 -->
          <div class="card shadow-sm border-0 rounded-3 mt-4">
            <div class="card-header bg-info text-white py-3 rounded-top-3">
              <h5 class="mb-0 fw-bold"><i class="fas fa-chart-bar me-2"></i>博客统计</h5>
            </div>
            <div class="card-body">
              <div class="row text-center">
                <div class="col-6">
                  <div class="display-6 text-primary fw-bold">{{ posts.length }}</div>
                  <p class="mb-0 text-muted">文章数</p>
                </div>
                <div class="col-6">
                  <div class="display-6 text-success fw-bold">∞</div>
                  <p class="mb-0 text-muted">访客数</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </main>

    <footer class="bg-dark text-white py-5 mt-5">
      <div class="container">
        <div class="row">
          <div class="col-md-6 mb-4 mb-md-0">
            <h5 class="fw-bold"><i class="fas fa-blog me-2"></i>我的个人博客</h5>
            <p class="mb-0 opacity-75">记录生活点滴，分享技术心得</p>
          </div>
          <div class="col-md-3 mb-4 mb-md-0">
            <h6 class="fw-bold">导航</h6>
            <ul class="list-unstyled">
              <li><a href="#" class="text-decoration-none text-white-50 opacity-75">首页</a></li>
              <li><a href="#" class="text-decoration-none text-white-50 opacity-75">归档</a></li>
              <li><a href="#" class="text-decoration-none text-white-50 opacity-75">关于</a></li>
            </ul>
          </div>
          <div class="col-md-3">
            <h6 class="fw-bold">联系我</h6>
            <ul class="list-unstyled">
              <li><a href="#" class="text-decoration-none text-white-50 opacity-75"><i class="fab fa-github me-2"></i>GitHub</a></li>
              <li><a href="#" class="text-decoration-none text-white-50 opacity-75"><i class="fab fa-twitter me-2"></i>Twitter</a></li>
              <li><a href="#" class="text-decoration-none text-white-50 opacity-75"><i class="fas fa-envelope me-2"></i>Email</a></li>
            </ul>
          </div>
        </div>
        <hr class="my-4 opacity-10">
        <div class="text-center text-white-50 opacity-75">
          <p class="mb-0">&copy; 2025 我的个人博客. All rights reserved.</p>
        </div>
      </div>
    </footer>
  </div>
</template>

<script>
import axios from 'axios'

export default {
  name: 'App',
  data() {
    return {
      showCreateForm: false,
      loading: true,
      posts: [],
      newPost: {
        title: '',
        content: '',
        author: ''
      }
    }
  },
  mounted() {
    this.loadPosts()
  },
  methods: {
    async loadPosts() {
      try {
        const response = await axios.get('/api/posts')
        this.posts = response.data
        this.loading = false
      } catch (error) {
        console.error('加载文章失败:', error)
        this.loading = false
        // 显示错误提示
        this.$toast && this.$toast.error('加载文章失败')
      }
    },
    async createPost() {
      try {
        // 验证输入
        if (!this.newPost.title.trim()) {
          alert('请输入文章标题')
          return
        }
        if (!this.newPost.author.trim()) {
          alert('请输入作者姓名')
          return
        }
        if (!this.newPost.content.trim()) {
          alert('请输入文章内容')
          return
        }

        await axios.post('/api/posts', this.newPost)
        // 重置表单
        this.newPost = { title: '', content: '', author: '' }
        this.showCreateForm = false
        // 重新加载文章列表
        this.loadPosts()

        // 显示成功消息
        alert('文章发布成功！')
      } catch (error) {
        console.error('发布文章失败:', error)
        alert('发布文章失败，请重试')
      }
    },
    formatDate(dateString) {
      const date = new Date(dateString)
      return date.toLocaleDateString('zh-CN') + ' ' + date.toLocaleTimeString('zh-CN')
    },
    truncateContent(content, maxLength) {
      if (!content) return ''
      if (content.length <= maxLength) return content
      return content.substring(0, maxLength) + '...'
    },
    viewFullPost(post) {
      // 这里可以实现查看完整文章的功能
      alert(`文章标题: ${post.title}\n作者: ${post.author}\n\n${post.content}`)
    }
  }
}
</script>

