/* eslint-disable */
<template>
  <div id="app">
    <header class="bg-primary text-white py-4 shadow-sm">
      <div class="container">
        <div class="row align-items-center">
          <div class="col-md-6">
            <h1 class="h3 mb-0"><i class="fas fa-blog me-2"></i>我的个人博客</h1>
          </div>
          <div class="col-md-6 text-md-end">
            <button class="btn btn-light" @click="showCreateForm = !showCreateForm">
              <i class="fas fa-plus-circle me-1"></i>{{ showCreateForm ? '取消' : '写文章' }}
            </button>
          </div>
        </div>
      </div>
    </header>

    <main class="container my-4">
      <!-- 发布文章表单 -->
      <div v-if="showCreateForm" class="card mb-4 shadow-sm">
        <div class="card-header bg-white">
          <h5 class="mb-0">发布新文章</h5>
        </div>
        <div class="card-body">
          <form @submit.prevent="createPost">
            <div class="mb-3">
              <label for="title" class="form-label">标题</label>
              <input type="text" class="form-control" id="title" v-model="newPost.title" required>
            </div>
            <div class="mb-3">
              <label for="author" class="form-label">作者</label>
              <input type="text" class="form-control" id="author" v-model="newPost.author" required>
            </div>
            <div class="mb-3">
              <label for="content" class="form-label">内容</label>
              <textarea class="form-control" id="content" rows="5" v-model="newPost.content" required></textarea>
            </div>
            <button type="submit" class="btn btn-primary">
              <i class="fas fa-paper-plane me-1"></i>发布文章
            </button>
          </form>
        </div>
      </div>

      <!-- 文章列表 -->
      <div class="row">
        <div class="col-md-8">
          <div v-if="loading" class="text-center py-5">
            <div class="spinner-border text-primary" role="status">
              <span class="visually-hidden">加载中...</span>
            </div>
          </div>

          <div v-else>
            <div v-if="posts.length === 0" class="alert alert-info">
              暂无文章，快来发表第一篇文章吧！
            </div>

            <article v-for="post in posts" :key="post.id" class="card mb-4 shadow-sm">
              <div class="card-body">
                <h2 class="card-title h4">{{ post.title }}</h2>
                <div class="d-flex justify-content-between text-muted mb-2">
                  <small><i class="fas fa-user me-1"></i>{{ post.author }}</small>
                  <small><i class="far fa-clock me-1"></i>{{ formatDate(post.createdTime) }}</small>
                </div>
                <p class="card-text">{{ post.content }}</p>
              </div>
            </article>
          </div>
        </div>

        <!-- 侧边栏 -->
        <div class="col-md-4">
          <div class="card shadow-sm">
            <div class="card-header bg-white">
              <h5 class="mb-0">关于博客</h5>
            </div>
            <div class="card-body">
              <p>这是一个简单的个人博客系统。</p>
              <p>访客可以直接发表文章，无需注册登录。</p>
              <ul class="list-unstyled">
                <li><i class="fas fa-check-circle text-success me-2"></i>简洁美观的设计</li>
                <li><i class="fas fa-check-circle text-success me-2"></i>响应式布局</li>
                <li><i class="fas fa-check-circle text-success me-2"></i>支持访客发帖</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </main>

    <footer class="bg-light py-4 mt-5 border-top">
      <div class="container text-center text-muted">
        <p class="mb-0">&copy; 2025 我的个人博客. All rights reserved.</p>
      </div>
    </footer>
  </div>
</template>

<script>
import axios from 'axios'

export default {
  name: 'App',
  data() {
    return {
      showCreateForm: false,
      loading: true,
      posts: [],
      newPost: {
        title: '',
        content: '',
        author: ''
      }
    }
  },
  mounted() {
    this.loadPosts()
  },
  methods: {
    async loadPosts() {
      try {
        const response = await axios.get('http://localhost:8080/api/posts')
        this.posts = response.data
        this.loading = false
      } catch (error) {
        console.error('加载文章失败:', error)
        this.loading = false
      }
    },
    async createPost() {
      try {
        await axios.post('http://localhost:8080/api/posts', this.newPost)
        // 重置表单
        this.newPost = { title: '', content: '', author: '' }
        this.showCreateForm = false
        // 重新加载文章列表
        this.loadPosts()
      } catch (error) {
        console.error('发布文章失败:', error)
        alert('发布文章失败，请重试')
      }
    },
    formatDate(dateString) {
      const date = new Date(dateString)
      return date.toLocaleDateString('zh-CN') + ' ' + date.toLocaleTimeString('zh-CN')
    }
  }
}
</script>

<style>
#app {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

.card {
  transition: transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out;
}

.card:hover {
  transform: translateY(-5px);
  box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15) !important;
}

.card-title {
  color: #333;
  font-weight: bold;
}

.btn {
  border-radius: 20px;
}

footer {
  margin-top: auto;
}
</style>
